mapper.select:
select pi.patient_id "patientId",visit_id "visitId",pi.name,pi.sex,pi.inp_no "inpNo",pi.bed_no "bedNo",pi.ward_code "wardCode", pi.ward_name "wardName",pi.doctor_in_charge "doctorCode",pi.date_of_birth "birthDay",pi.admission_date_time "admissionDate", pi.diagnosis from patient_info pi where 1=1
and pi.dept_code = #{deptCode}
and pi.ward_code = #{wardCode}
and (pi.name like concat(concat('%',#{keyword}),'%') or to_char(pi.bed_no) like concat(concat('%',#{keyword}),'%') or pi.inp_no like concat(concat('%',#{keyword}),'%'))

select *
from orders_m
where patient_id = #{patientId} and visit_id = #{visitId}
and repeat_indicator = 0
and start_date_time >= #{startDateOfDay} and start_date_time <= #{endDateOfDay}

    and administration in

        #{item}



    and order_status in

        #{status}


order by order_no

select *
from orders_m
where patient_id = #{patientId} and visit_id = #{visitId}
and start_date_time <= #{endTime} and repeat_indicator = 1

    and administration in

        #{item}



    and order_status in

        #{status}


